import React from 'react';
import { Line } from 'react-chartjs-2';
import Chart from 'chart.js';

var questions = ["64.2", "52.5"," 70.25", "77.2", "67.6"," 74.25", "63.2", "68.4"];
var questionLabels = ["Sheet 1 Introduction", "Sheet 2 Material derivative", "Sheet 3 Continuity", "Sheet 4 The Euler equations", "Sheet 5 Dimensional analysis", "Sheet 6 Speed of sound", "Sheet 7 Isentropic flow relations", "Sheet 8 Compressible flow through nozzles"];

// LineChart
class SheetLineChart extends React.Component {
    constructor(props) {
      super(props);
      this.canvasRef = React.createRef();
    }
  
    componentDidMount() {
      this.myChart = new Chart(this.canvasRef.current, {
        type: 'line',
        options: {
            title: {
                display: true,
                text: 'ME2 Tutorial Sheet Average Grades',
                fontSize: 10
            },
            legend: {
                //display: flase, doesn't display legend
                position: 'right',
                labels: {
                    fontColor: '#000'
                }
            },
        },
        data: {
            labels: questionLabels, //sets x axis to student names 
            datasets: [{
            label: 'Average Score',
            data: questions, //converts total score to a percentage
            backgroundColor: [
                'rgba(255, 99, 132, 0.2)',
                'rgba(54, 162, 235, 0.2)',
                'rgba(255, 206, 86, 0.2)',
                'rgba(75, 192, 192, 0.2)',
                'rgba(153, 102, 255, 0.2)',
                'rgba(255, 159, 64, 0.2)'
            ],
            borderColor: [
                'rgba(255, 99, 132, 1)',
                'rgba(54, 162, 235, 1)',
                'rgba(255, 206, 86, 1)',
                'rgba(75, 192, 192, 1)',
                'rgba(153, 102, 255, 1)',
                'rgba(255, 159, 64, 1)'
            ],
            borderWidth: 3,
            borderColor: '#777',
            hoverBorderWidth: 3,
            hoverBorderColor: '#00',
            fill: true
          }]
        }
      });
    }
  
    render() {
      return <canvas ref={this.canvasRef} />;
    }
  }

  export default SheetLineChart
