import React from 'react';
// import { Radar } from 'react-chartjs-2';
import Chart from 'chart.js';

var questions = ["81.82", "85.31"," 58.61"];
var questionLabels = ["Q1-Continuity Equation", "Q2-Scaling relationship", "Q3-Flow in a rotating cylinder"];
// LineChart
class RadarChart extends React.Component {
    constructor(props) {
      super(props);
      this.canvasRef = React.createRef();
    }

    componentDidMount() {
      this.myChart = new Chart(this.canvasRef.current, {
        type: 'radar',
        options: {
            title: {
                display: true,
                text: 'ME2 Fluids 2020 Progress Test: Average % Score per Question',
                fontSize: 10
            },
            legend: {
                //display: flase, doesn't display legend
                position: 'right',
                labels: {
                    fontColor: '#000'
                }
            },
        },
        data: {
          labels: questionLabels,
          datasets: [{
            label: 'Average Score',
            data: questions,
            fill: true,
            backgroundColor: [
                'rgba(255, 99, 132, 0.2)',
                'rgba(54, 162, 235, 0.2)',
                'rgba(255, 206, 86, 0.2)',
                'rgba(75, 192, 192, 0.2)',
                'rgba(153, 102, 255, 0.2)',
                'rgba(255, 159, 64, 0.2)'
            ],
            pointRadius: 2,
            borderColor: [
                'rgba(255, 99, 132, 1)',
                'rgba(54, 162, 235, 1)',
                'rgba(255, 206, 86, 1)',
                'rgba(75, 192, 192, 1)',
                'rgba(153, 102, 255, 1)',
                'rgba(255, 159, 64, 1)'
            ],
            borderWidth: 1,
            hoverBorderWidth: 3,
            hoverBorderColor: '#00',
            lineTension: 0
          }]
        }
      });
    }
    render() {
      return <canvas ref={this.canvasRef} />;
    }
  }

  export default RadarChart
